export async function getStaticPaths() {
  try {
    // Fetch first 100 posts for pre-rendering
    const { posts } = await fetchBlogPosts(1, 100);

    // Create paths for each post using direct slug
    const paths = posts.map((post) => {
      const formattedPost = formatWordPressPost(post);
      return {
        params: { 
          slug: formattedPost.wpSlug || formattedPost.slug 
        }
      };
    });

    return {
      paths,
      // Enable fallback for posts not included in paths
      fallback: true,
    };
  } catch (error) {
    console.error('Error generating paths:', error);
    return { paths: [], fallback: true };
  }
}

export async function getStaticProps({ params }) {
  try {
    // Fetch the main post
    const postData = await fetchPostBySlug(params.slug);

    if (!postData) {
      return {
        notFound: true,
      };
    }

    const post = formatWordPressPost(postData);

    // Fetch related posts (same category if possible)
    const relatedResult = await fetchBlogPosts(1, 3);
    let relatedPosts = formatWordPressPosts(relatedResult.posts)
      .filter(p => p.id !== post.id)
      .slice(0, 3);

    return {
      props: {
        post,
        relatedPosts,
      },
      revalidate: 60, // Revalidate pages after 60 seconds
    };
  } catch (error) {
    console.error('Error fetching blog post:', error);
    return {
      notFound: true
    };
  }
}

