import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";

export function registerRoutes(app: Express): Server {

  // Priority Content routes
  app.get("/api/priority-content", async (req, res) => {
    try {
      // Return all priority content without user filtering
      const contentItems = await storage.getAllPriorityContent();
      res.status(200).json(contentItems);
    } catch (error) {
      console.error("Error fetching priority content:", error);
      res.status(500).json({ message: "Error fetching priority content" });
    }
  });

  app.post("/api/priority-content", async (req, res) => {
    try {
      const newContent = await storage.createPriorityContent({
        ...req.body,
        userId: 1, // Default user ID for no-auth mode
      });
      res.status(201).json(newContent);
    } catch (error) {
      console.error("Error creating priority content:", error);
      res.status(500).json({ message: "Error creating priority content" });
    }
  });

  app.put("/api/priority-content/:id", async (req: any, res: any) => {
    try {
      const contentId = parseInt(req.params.id, 10);
      const content = await storage.getPriorityContentById(contentId);

      if (!content) {
        return res.status(404).json({ message: "Content not found" });
      }

      const updatedContent = await storage.updatePriorityContent(contentId, req.body);
      res.status(200).json(updatedContent);
    } catch (error) {
      console.error("Error updating priority content:", error);
      res.status(500).json({ message: "Error updating priority content" });
    }
  });

  app.delete("/api/priority-content/:id", async (req: any, res: any) => {
    try {
      const contentId = parseInt(req.params.id, 10);
      const content = await storage.getPriorityContentById(contentId);

      if (!content) {
        return res.status(404).json({ message: "Content not found" });
      }

      await storage.deletePriorityContent(contentId);
      res.status(204).end();
    } catch (error) {
      console.error("Error deleting priority content:", error);
      res.status(500).json({ message: "Error deleting priority content" });
    }
  });

  // Links routes
  app.get("/api/links", async (req, res) => {
    try {
      const links = await storage.getAllLinks();
      res.status(200).json(links);
    } catch (error) {
      console.error("Error fetching links:", error);
      res.status(500).json({ message: "Error fetching links" });
    }
  });

  app.post("/api/links", async (req, res) => {
    try {
      const newLink = await storage.createLink({
        ...req.body,
        userId: 1, // Default user ID for no-auth mode
      });
      res.status(201).json(newLink);
    } catch (error) {
      console.error("Error creating link:", error);
      res.status(500).json({ message: "Error creating link" });
    }
  });

  // Domains routes
  app.get("/api/domains", async (req, res) => {
    try {
      const domains = await storage.getDomains();
      res.status(200).json(domains);
    } catch (error) {
      console.error("Error fetching domains:", error);
      res.status(500).json({ message: "Error fetching domains" });
    }
  });

  // Campaigns routes
  app.get("/api/campaigns", async (req, res) => {
    try {
      const campaigns = await storage.getAllCampaigns();
      res.status(200).json(campaigns);
    } catch (error) {
      console.error("Error fetching campaigns:", error);
      res.status(500).json({ message: "Error fetching campaigns" });
    }
  });

  app.post("/api/campaigns", async (req, res) => {
    try {
      const newCampaign = await storage.createCampaign({
        ...req.body,
        userId: 1, // Default user ID for no-auth mode
      });
      res.status(201).json(newCampaign);
    } catch (error) {
      console.error("Error creating campaign:", error);
      res.status(500).json({ message: "Error creating campaign" });
    }
  });

  // Campaign domains routes
  app.post("/api/campaigns/:campaignId/domains", async (req: any, res: any) => {
    try {
      const campaignId = parseInt(req.params.campaignId, 10);
      const domainId = req.body.domainId;

      const campaign = await storage.getCampaignById(campaignId);
      if (!campaign) {
        return res.status(404).json({ message: "Campaign not found" });
      }

      const campaignDomain = await storage.addDomainToCampaign({
        campaignId,
        domainId,
      });
      res.status(201).json(campaignDomain);
    } catch (error) {
      console.error("Error adding domain to campaign:", error);
      res.status(500).json({ message: "Error adding domain to campaign" });
    }
  });

  app.get("/api/campaigns/:campaignId/domains", async (req: any, res: any) => {
    try {
      const campaignId = parseInt(req.params.campaignId, 10);

      const campaign = await storage.getCampaignById(campaignId);
      if (!campaign) {
        return res.status(404).json({ message: "Campaign not found" });
      }

      const domains = await storage.getCampaignDomains(campaignId);
      res.status(200).json(domains);
    } catch (error) {
      console.error("Error fetching campaign domains:", error);
      res.status(500).json({ message: "Error fetching campaign domains" });
    }
  });

  // Booking request routes
  app.post("/api/booking", async (req, res) => {
    try {
      const bookingRequest = await storage.createBookingRequest({
        ...req.body,
        userId: undefined, // No user context in no-auth mode
      });
      res.status(201).json(bookingRequest);
    } catch (error) {
      console.error("Error creating booking request:", error);
      res.status(500).json({ message: "Error creating booking request" });
    }
  });

  // User settings routes - disabled in no-auth mode
  app.get("/api/user/settings", async (req, res) => {
    res.status(200).json({
      name: "Quick Access User",
      email: "user@example.com",
      username: "quickuser",
    });
  });

  app.put("/api/user/settings", async (req, res) => {
    // User settings disabled in no-auth mode
    res.status(200).json({
      name: "Quick Access User",
      email: "user@example.com", 
      username: "quickuser",
    });
  });

  // Create HTTP server
  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { users, type User, type InsertUser, type BookingRequest, type InsertBookingRequest, bookingRequests, type PriorityContent, type InsertPriorityContent, priorityContent, type Link, type InsertLink, links, type Domain, type InsertDomain, domains, type Campaign, type InsertCampaign, campaigns, type CampaignDomain, type InsertCampaignDomain, campaignDomains } from "../shared/schema";
import { db } from "./db";
import { eq, desc } from "drizzle-orm";

export interface IStorage {
  // User methods
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(insertUser: InsertUser): Promise<User>;
  updateUser(id: number, userData: Partial<User>): Promise<User | undefined>;

  // Booking request methods
  createBookingRequest(insertBookingRequest: InsertBookingRequest): Promise<BookingRequest>;
  getBookingRequests(userId: number): Promise<BookingRequest[]>;
  getBookingRequestById(id: number): Promise<BookingRequest | undefined>;

  // Priority content methods
  createPriorityContent(insertPriorityContent: InsertPriorityContent): Promise<PriorityContent>;
  getPriorityContent(userId: number): Promise<PriorityContent[]>;
  getAllPriorityContent(): Promise<PriorityContent[]>;
  getPriorityContentById(id: number): Promise<PriorityContent | undefined>;
  updatePriorityContent(id: number, contentData: Partial<PriorityContent>): Promise<PriorityContent | undefined>;
  deletePriorityContent(id: number): Promise<boolean>;

  // Links methods
  createLink(insertLink: InsertLink): Promise<Link>;
  getLinks(userId: number): Promise<Link[]>;
  getAllLinks(): Promise<Link[]>;
  getLinkById(id: number): Promise<Link | undefined>;
  updateLink(id: number, linkData: Partial<Link>): Promise<Link | undefined>;
  deleteLink(id: number): Promise<boolean>;

  // Domains methods
  createDomain(insertDomain: InsertDomain): Promise<Domain>;
  getDomains(): Promise<Domain[]>;
  getDomainById(id: number): Promise<Domain | undefined>;
  updateDomain(id: number, domainData: Partial<Domain>): Promise<Domain | undefined>;
  deleteDomain(id: number): Promise<boolean>;

  // Campaigns methods
  createCampaign(insertCampaign: InsertCampaign): Promise<Campaign>;
  getCampaigns(userId: number): Promise<Campaign[]>;
  getAllCampaigns(): Promise<Campaign[]>;
  getCampaignById(id: number): Promise<Campaign | undefined>;
  updateCampaign(id: number, campaignData: Partial<Campaign>): Promise<Campaign | undefined>;
  deleteCampaign(id: number): Promise<boolean>;

  // Campaign Domains methods
  addDomainToCampaign(insertCampaignDomain: InsertCampaignDomain): Promise<CampaignDomain>;
  removeDomainFromCampaign(campaignId: number, domainId: number): Promise<boolean>;
  getCampaignDomains(campaignId: number): Promise<Domain[]>;
}

export class DatabaseStorage implements IStorage {
  constructor() {
    // No session store needed in no-auth mode
  }

  // User methods
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async updateUser(id: number, userData: Partial<User>): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set({ ...userData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Booking request methods
  async createBookingRequest(insertBookingRequest: InsertBookingRequest): Promise<BookingRequest> {
    const [bookingRequest] = await db.insert(bookingRequests).values(insertBookingRequest).returning();
    return bookingRequest;
  }

  async getBookingRequests(userId: number): Promise<BookingRequest[]> {
    return db.select().from(bookingRequests).where(eq(bookingRequests.userId, userId)).orderBy(desc(bookingRequests.createdAt));
  }

  async getBookingRequestById(id: number): Promise<BookingRequest | undefined> {
    const [bookingRequest] = await db.select().from(bookingRequests).where(eq(bookingRequests.id, id));
    return bookingRequest;
  }

  // Priority content methods
  async createPriorityContent(insertPriorityContent: InsertPriorityContent): Promise<PriorityContent> {
    const [content] = await db.insert(priorityContent).values(insertPriorityContent).returning();
    return content;
  }

  async getPriorityContent(userId: number): Promise<PriorityContent[]> {
    return db.select().from(priorityContent).where(eq(priorityContent.userId, userId));
  }

  async getAllPriorityContent(): Promise<PriorityContent[]> {
    return db.select().from(priorityContent).orderBy(desc(priorityContent.createdAt));
  }

  async getPriorityContentById(id: number): Promise<PriorityContent | undefined> {
    const [content] = await db.select().from(priorityContent).where(eq(priorityContent.id, id));
    return content;
  }

  async updatePriorityContent(id: number, contentData: Partial<PriorityContent>): Promise<PriorityContent | undefined> {
    const [content] = await db
      .update(priorityContent)
      .set({ ...contentData, updatedAt: new Date() })
      .where(eq(priorityContent.id, id))
      .returning();
    return content;
  }

  async deletePriorityContent(id: number): Promise<boolean> {
    const result = await db.delete(priorityContent).where(eq(priorityContent.id, id));
    return !!result;
  }

  // Links methods
  async createLink(insertLink: InsertLink): Promise<Link> {
    const [link] = await db.insert(links).values(insertLink).returning();
    return link;
  }

  async getLinks(userId: number): Promise<Link[]> {
    return db.select().from(links).where(eq(links.userId, userId)).orderBy(desc(links.date));
  }

  async getAllLinks(): Promise<Link[]> {
    return db.select().from(links).orderBy(desc(links.date));
  }

  async getLinkById(id: number): Promise<Link | undefined> {
    const [link] = await db.select().from(links).where(eq(links.id, id));
    return link;
  }

  async updateLink(id: number, linkData: Partial<Link>): Promise<Link | undefined> {
    const [link] = await db
      .update(links)
      .set({ ...linkData, updatedAt: new Date() })
      .where(eq(links.id, id))
      .returning();
    return link;
  }

  async deleteLink(id: number): Promise<boolean> {
    const result = await db.delete(links).where(eq(links.id, id));
    return !!result;
  }

  // Domains methods
  async createDomain(insertDomain: InsertDomain): Promise<Domain> {
    const [domain] = await db.insert(domains).values(insertDomain).returning();
    return domain;
  }

  async getDomains(): Promise<Domain[]> {
    return db.select().from(domains).orderBy(desc(domains.drScore));
  }

  async getDomainById(id: number): Promise<Domain | undefined> {
    const [domain] = await db.select().from(domains).where(eq(domains.id, id));
    return domain;
  }

  async updateDomain(id: number, domainData: Partial<Domain>): Promise<Domain | undefined> {
    const [domain] = await db
      .update(domains)
      .set({ ...domainData, updatedAt: new Date() })
      .where(eq(domains.id, id))
      .returning();
    return domain;
  }

  async deleteDomain(id: number): Promise<boolean> {
    const result = await db.delete(domains).where(eq(domains.id, id));
    return !!result;
  }

  // Campaigns methods
  async createCampaign(insertCampaign: InsertCampaign): Promise<Campaign> {
    const [campaign] = await db.insert(campaigns).values(insertCampaign).returning();
    return campaign;
  }

  async getCampaigns(userId: number): Promise<Campaign[]> {
    return db.select().from(campaigns).where(eq(campaigns.userId, userId));
  }

  async getAllCampaigns(): Promise<Campaign[]> {
    return db.select().from(campaigns).orderBy(desc(campaigns.createdAt));
  }

  async getCampaignById(id: number): Promise<Campaign | undefined> {
    const [campaign] = await db.select().from(campaigns).where(eq(campaigns.id, id));
    return campaign;
  }

  async updateCampaign(id: number, campaignData: Partial<Campaign>): Promise<Campaign | undefined> {
    const [campaign] = await db
      .update(campaigns)
      .set({ ...campaignData, updatedAt: new Date() })
      .where(eq(campaigns.id, id))
      .returning();
    return campaign;
  }

  async deleteCampaign(id: number): Promise<boolean> {
    const result = await db.delete(campaigns).where(eq(campaigns.id, id));
    return !!result;
  }

  // Campaign Domains methods
  async addDomainToCampaign(insertCampaignDomain: InsertCampaignDomain): Promise<CampaignDomain> {
    const [campaignDomain] = await db.insert(campaignDomains).values(insertCampaignDomain).returning();
    return campaignDomain;
  }

  async removeDomainFromCampaign(campaignId: number, domainId: number): Promise<boolean> {
    const result = await db
      .delete(campaignDomains)
      .where(eq(campaignDomains.campaignId, campaignId) && eq(campaignDomains.domainId, domainId));
    return !!result;
  }

  async getCampaignDomains(campaignId: number): Promise<Domain[]> {
    const campaignDomainData = await db
      .select({
        domain: domains,
      })
      .from(campaignDomains)
      .where(eq(campaignDomains.campaignId, campaignId))
      .innerJoin(domains, eq(campaignDomains.domainId, domains.id));

    return campaignDomainData.map((item) => item.domain);
  }
}

export const storage = new DatabaseStorage();

shared/schema.ts.content:
import { pgTable, serial, text, integer, timestamp, boolean, varchar, pgEnum } from 'drizzle-orm/pg-core';
import { relations, sql } from 'drizzle-orm';

// Users table (for authentication and user information)
export const users = pgTable('users', {
  id: serial('id').primaryKey(),
  username: text('username').notNull().unique(),
  email: text('email').notNull().unique(),
  password: text('password'),
  name: text('name'),
  supabaseId: text('supabase_id').unique(),
  isTemporary: boolean('is_temporary').default(false),
  lastLogin: timestamp('last_login'),
  createdAt: timestamp('created_at').default(sql`NOW()`),
  updatedAt: timestamp('updated_at').default(sql`NOW()`),
});

// Booking requests table (for strategy calls)
export const bookingRequests = pgTable('booking_requests', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id, { onDelete: 'cascade' }),
  firstName: text('first_name').notNull(),
  lastName: text('last_name').notNull(),
  email: text('email').notNull(),
  company: text('company').notNull(),
  website: text('website'),
  monthlyTraffic: text('monthly_traffic'),
  currentLinks: text('current_links'),
  goals: text('goals'),
  date: text('date').notNull(),
  time: text('time').notNull(),
  timezone: text('timezone').notNull(),
  status: text('status').default('pending').notNull(), // pending, confirmed, completed, cancelled
  createdAt: timestamp('created_at').default(sql`NOW()`),
  updatedAt: timestamp('updated_at').default(sql`NOW()`),
});

// Content Type Enum
export const contentTypeEnum = pgEnum('content_type', ['Money Page', 'Case Study', 'Blog Post', 'Landing Page']);

// Goal Type Enum
export const goalTypeEnum = pgEnum('goal_type', ['SEO', 'Conversions', 'Both']);

// Status Type Enum
export const statusEnum = pgEnum('status', ['Active', 'Paused', 'Completed']);

// Priority Content table
export const priorityContent = pgTable('priority_content', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id, { onDelete: 'cascade' }),
  url: text('url').notNull(),
  contentType: contentTypeEnum('content_type').notNull(),
  goal: goalTypeEnum('goal').notNull(),
  status: statusEnum('status').default('Active').notNull(),
  targetKeyword: text('target_keyword'),
  createdAt: timestamp('created_at').default(sql`NOW()`),
  updatedAt: timestamp('updated_at').default(sql`NOW()`),
});

// Link Status Enum
export const linkStatusEnum = pgEnum('link_status', ['Live', 'Submitted', 'Progress', 'Planned']);

// Link Type Enum
export const linkTypeEnum = pgEnum('link_type', ['Editorial', 'Guest Post']);

// Links table
export const links = pgTable('links', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id, { onDelete: 'cascade' }),
  date: timestamp('date').default(sql`NOW()`),
  domain: text('domain').notNull(),
  domainRating: integer('domain_rating'),
  linkFrom: text('link_from').notNull(),
  linkTo: text('link_to').notNull(),
  anchorText: text('anchor_text'),
  status: linkStatusEnum('status').default('Planned').notNull(),
  type: linkTypeEnum('type').notNull(),
  price: integer('price'),
  createdAt: timestamp('created_at').default(sql`NOW()`),
  updatedAt: timestamp('updated_at').default(sql`NOW()`),
});

// Domains table
export const domains = pgTable('domains', {
  id: serial('id').primaryKey(),
  domain: text('domain').notNull().unique(),
  drScore: integer('dr_score'),
  monthlyTraffic: text('monthly_traffic'),
  price: integer('price'),
  linkType: linkTypeEnum('link_type').notNull(),
  isSelected: boolean('is_selected').default(false),
  createdAt: timestamp('created_at').default(sql`NOW()`),
  updatedAt: timestamp('updated_at').default(sql`NOW()`),
});

// Campaigns table
export const campaigns = pgTable('campaigns', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id, { onDelete: 'cascade' }),
  name: text('name').notNull(),
  goal: text('goal'),
  budget: integer('budget'),
  status: text('status').default('active'),
  createdAt: timestamp('created_at').default(sql`NOW()`),
  updatedAt: timestamp('updated_at').default(sql`NOW()`),
});

// Campaign Domains junction table
export const campaignDomains = pgTable('campaign_domains', {
  id: serial('id').primaryKey(),
  campaignId: integer('campaign_id').references(() => campaigns.id, { onDelete: 'cascade' }),
  domainId: integer('domain_id').references(() => domains.id, { onDelete: 'cascade' }),
  createdAt: timestamp('created_at').default(sql`NOW()`),
});

// Relations
export const usersRelations = relations(users, ({ many }) => ({
  bookingRequests: many(bookingRequests),
  priorityContent: many(priorityContent),
  links: many(links),
  campaigns: many(campaigns),
}));

export const bookingRequestsRelations = relations(bookingRequests, ({ one }) => ({
  user: one(users, {
    fields: [bookingRequests.userId],
    references: [users.id],
  }),
}));

export const priorityContentRelations = relations(priorityContent, ({ one }) => ({
  user: one(users, {
    fields: [priorityContent.userId],
    references: [users.id],
  }),
}));

export const linksRelations = relations(links, ({ one }) => ({
  user: one(users, {
    fields: [links.userId],
    references: [users.id],
  }),
}));

export const campaignsRelations = relations(campaigns, ({ one, many }) => ({
  user: one(users, {
    fields: [campaigns.userId],
    references: [users.id],
  }),
  domains: many(campaignDomains),
}));

export const campaignDomainsRelations = relations(campaignDomains, ({ one }) => ({
  campaign: one(campaigns, {
    fields: [campaignDomains.campaignId],
    references: [campaigns.id],
  }),
  domain: one(domains, {
    fields: [campaignDomains.domainId],
    references: [domains.id],
  }),
}));

export const domainsRelations = relations(domains, ({ many }) => ({
  campaigns: many(campaignDomains),
}));

// Types for Insert/Select operations
export type User = typeof users.$inferSelect;
export type InsertUser = typeof users.$inferInsert;

export type BookingRequest = typeof bookingRequests.$inferSelect;
export type InsertBookingRequest = typeof bookingRequests.$inferInsert;

export type PriorityContent = typeof priorityContent.$inferSelect;
export type InsertPriorityContent = typeof priorityContent.$inferInsert;

export type Link = typeof links.$inferSelect;
export type InsertLink = typeof links.$inferInsert;

export type Domain = typeof domains.$inferSelect;
export type InsertDomain = typeof domains.$inferInsert;

export type Campaign = typeof campaigns.$inferSelect;
export type InsertCampaign = typeof campaigns.$inferInsert;

export type CampaignDomain = typeof campaignDomains.$inferSelect;
export type InsertCampaignDomain = typeof campaignDomains.$inferInsert;

