components/blog/BlogGrid.js.content:
import React from 'react';
import Link from 'next/link';
import Image from 'next/image';
import { CalendarIcon, ClockIcon } from '@heroicons/react/24/outline';

const BlogGrid = ({ posts, showPagination = false, totalPages = 1, currentPage = 1 }) => {
  if (!posts || posts.length === 0) {
    return (
      <div className="text-center py-12">
        <p className="text-secondary-600">No blog posts found.</p>
      </div>
    );
  }

  return (
    <div>
      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
        {posts.map((post) => (
          <article 
            key={post.id} 
            className="bg-white border border-black rounded-xl overflow-hidden hover:shadow-medium transition-shadow"
          >
            <Link href={post.wpSlug ? `/${post.wpSlug}` : `/${post.slug}`}>
              <div className="aspect-w-16 aspect-h-9 relative">
                <Image
                  src={post.featuredImage}
                  alt={post.title}
                  fill
                  className="object-cover"
                  sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
                />
              </div>
              <div className="p-6">
                <div className="flex items-center text-sm text-black mb-2">
                  <span className="inline-block px-3 py-1 bg-primary-50 text-primary-600 rounded-full font-medium text-xs">
                    {post.category}
                  </span>
                </div>
                <h3 className="text-xl font-semibold text-black mb-2 line-clamp-2">
                  {post.title}
                </h3>
                <p className="text-black mb-4 line-clamp-2">
                  {post.excerpt && post.excerpt.split(' ').slice(0, 20).join(' ')}
                  {post.excerpt && post.excerpt.split(' ').length > 20 ? '...' : ''}
                </p>
                <div className="flex items-center text-sm text-black">
                  <CalendarIcon className="h-4 w-4 mr-1" aria-hidden="true" />
                  <span>{post.date}</span>
                  <span className="mx-2"></span>
                  <ClockIcon className="h-4 w-4 mr-1" aria-hidden="true" />
                  <span>{post.readTime}</span>
                </div>
              </div>
            </Link>
          </article>
        ))}
      </div>

      {showPagination && totalPages > 1 && (
        <div className="mt-12 flex justify-center">
          <nav className="inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
            <Link
              href={currentPage > 1 ? `/link-building-blog?page=${currentPage - 1}` : '#'}
              className={`relative inline-flex items-center px-4 py-2 border border-secondary-300 rounded-l-md ${
                currentPage === 1 
                  ? 'bg-secondary-50 text-secondary-400 cursor-not-allowed' 
                  : 'bg-white text-secondary-700 hover:bg-secondary-50'
              }`}
            >
              <span className="sr-only">Previous</span>
              <svg
                className="h-5 w-5"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 20 20"
                fill="currentColor"
                aria-hidden="true"
              >
                <path
                  fillRule="evenodd"
                  d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z"
                  clipRule="evenodd"
                />
              </svg>
            </Link>

            {[...Array(totalPages)].map((_, i) => {
              const page = i + 1;
              return (
                <Link
                  key={page}
                  href={`/link-building-blog?page=${page}`}
                  className={`relative inline-flex items-center px-4 py-2 border border-secondary-300 ${
                    currentPage === page
                      ? 'z-10 bg-primary-50 border-primary-500 text-primary-600'
                      : 'bg-white text-secondary-700 hover:bg-secondary-50'
                  }`}
                >
                  {page}
                </Link>
              );
            })}

            <Link
              href={currentPage < totalPages ? `/link-building-blog?page=${currentPage + 1}` : '#'}
              className={`relative inline-flex items-center px-4 py-2 border border-secondary-300 rounded-r-md ${
                currentPage === totalPages 
                  ? 'bg-secondary-50 text-secondary-400 cursor-not-allowed' 
                  : 'bg-white text-secondary-700 hover:bg-secondary-50'
              }`}
            >
              <span className="sr-only">Next</span>
              <svg
                className="h-5 w-5"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 20 20"
                fill="currentColor"
                aria-hidden="true"
              >
                <path
                  fillRule="evenodd"
                  d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z"
                  clipRule="evenodd"
                />
              </svg>
            </Link>
          </nav>
        </div>
      )}
    </div>
  );
};

export default BlogGrid;

components/blog/CommentSection.js.content:
import React, { useState } from 'react';

const CommentSection = () => {
  const [comments, setComments] = useState([]);
  const [name, setName] = useState('');
  const [comment, setComment] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitting(true);

    // Simulate API call
    setTimeout(() => {
      const newComment = {
        id: Date.now(),
        name,
        comment,
        date: new Date().toISOString(),
      };

      setComments([...comments, newComment]);
      setName('');
      setComment('');
      setSubmitting(false);
    }, 500);
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  return (
    <div className="mt-16">
      <h2 className="text-2xl font-bold mb-8">Comments</h2>

      <form onSubmit={handleSubmit} className="mb-10">
        <div className="mb-4">
          <label htmlFor="name" className="block text-secondary-700 mb-2">
            Your Name
          </label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your name"
            className="w-full px-4 py-2 border border-secondary-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="comment" className="block text-secondary-700 mb-2">
            Comment
          </label>
          <textarea
            id="comment"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Share your thoughts..."
            rows="4"
            className="w-full px-4 py-2 border border-secondary-300 rounded-md focus:ring-primary-500 focus:border-primary-500 resize-y"
            required
          />
        </div>

        <div className="text-right">
          <button
            type="submit"
            disabled={submitting}
            className="inline-flex items-center justify-center px-6 py-3 bg-secondary-700 text-white font-medium rounded-md shadow-sm hover:bg-secondary-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-secondary-500"
          >
            {submitting ? (
              <>
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Posting...
              </>
            ) : (
              'Post Comment'
            )}
          </button>
        </div>
      </form>

      {comments.length > 0 ? (
        <div className="space-y-6">
          {comments.map((comment) => (
            <div key={comment.id} className="bg-white p-4 rounded-lg border border-secondary-200">
              <div className="flex items-center mb-2">
                <div className="h-10 w-10 rounded-full bg-primary-100 text-primary-600 flex items-center justify-center mr-3">
                  <span className="font-semibold">{comment.name.charAt(0)}</span>
                </div>
                <div>
                  <h4 className="font-medium text-secondary-900">{comment.name}</h4>
                  <p className="text-sm text-secondary-500">{formatDate(comment.date)}</p>
                </div>
              </div>
              <p className="text-secondary-700 mt-2">{comment.comment}</p>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-secondary-500 italic">No comments yet. Be the first to share your thoughts!</p>
      )}
    </div>
  );
};

export default CommentSection;

components/blog/Pagination.js.content:
import React from 'react';
import Link from 'next/link';

const Pagination = ({ totalPages = 6, currentPage = 1 }) => {
  // Create an array of page numbers to display
  const pages = Array.from({ length: totalPages }, (_, i) => i + 1);

  // Define the page paths based on the page number
  const getPagePath = (pageNum) => {
    switch(pageNum) {
      case 1:
        return '/';
      case 2:
        return '/services';
      case 3:
        return '/case-studies';
      case 4:
        return '/pricing';
      case 5:
        return '/about-us';
      case 6:
        return '/resources';
      default:
        return `/page/${pageNum}`;
    }
  };

  return (
    <div className="flex justify-center items-center py-6 mt-4">
      <ul className="flex space-x-1">
        {pages.map(page => (
          <li key={page}>
            <Link
              href={getPagePath(page)}
              className={`inline-flex items-center justify-center w-8 h-8 ${
                currentPage === page
                  ? 'bg-black text-white'
                  : 'bg-white text-gray-800 hover:bg-gray-100'
              }`}
            >
              {page}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Pagination;

components/blog/TableOfContents.js.content:
import React, { useEffect, useState } from 'react';
import Link from 'next/link';

const TableOfContents = ({ content }) => {
  const [headings, setHeadings] = useState([]);
  const [activeId, setActiveId] = useState('');

  // Function to extract headings from HTML content
  useEffect(() => {
    if (!content) return;

    // Parse the content to extract headings
    const tempDiv = document.createElement('div');
    tempDiv.innerHTML = content;

    const headingElements = tempDiv.querySelectorAll('h2, h3, h4');
    const extractedHeadings = Array.from(headingElements).map((heading) => {
      // Generate an ID if it doesn't exist
      const id = heading.id || heading.textContent.trim().toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]/g, '');

      return {
        id,
        text: heading.textContent,
        level: parseInt(heading.tagName.charAt(1)),
      };
    });

    setHeadings(extractedHeadings);
  }, [content]);

  // Setup intersection observer to highlight active section
  useEffect(() => {
    if (headings.length === 0) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setActiveId(entry.target.id);
          }
        });
      },
      {
        rootMargin: '0px 0px -80% 0px',
      }
    );

    // Observe all headings
    headings.forEach((heading) => {
      const element = document.getElementById(heading.id);
      if (element) {
        observer.observe(element);
      }
    });

    return () => {
      headings.forEach((heading) => {
        const element = document.getElementById(heading.id);
        if (element) {
          observer.unobserve(element);
        }
      });
    };
  }, [headings]);

  // Function to determine indent class based on heading level
  const getIndentClass = (level) => {
    switch (level) {
      case 3:
        return 'ml-4';
      case 4:
        return 'ml-8';
      default:
        return '';
    }
  };

  return (
    <div className="bg-white rounded-lg p-5 shadow-sm sticky top-24">
      <h2 className="text-lg font-semibold mb-4 text-secondary-900">TABLE OF CONTENTS</h2>
      <nav>
        <ul className="space-y-2 text-sm">
          {headings.map((heading) => (
            <li key={heading.id} className={getIndentClass(heading.level)}>
              <Link
                href={`#${heading.id}`}
                className={`block py-1 border-l-2 pl-3 transition-colors hover:text-primary-600 ${
                  activeId === heading.id
                    ? 'border-primary-600 text-primary-600 font-medium'
                    : 'border-secondary-200 text-secondary-600'
                }`}
              >
                {heading.text}
              </Link>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
};

export default TableOfContents;

components/booking/BookingForm.js.content:
import { useState } from 'react';

const BookingForm = ({ onSubmitSuccess }) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    company: '',
    website: '',
    monthlyTraffic: '',
    currentLinks: '',
    goals: '',
    date: '',
    time: '',
    timezone: 'EST'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const nextStep = () => {
    setCurrentStep(currentStep + 1);
  };

  const prevStep = () => {
    setCurrentStep(currentStep - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // In a real application, this would send data to a backend
    console.log('Form submitted:', formData);
    onSubmitSuccess();
  };

  const timeSlots = [
    '9:00 AM', '9:30 AM', '10:00 AM', '10:30 AM', 
    '11:00 AM', '11:30 AM', '1:00 PM', '1:30 PM',
    '2:00 PM', '2:30 PM', '3:00 PM', '3:30 PM',
    '4:00 PM', '4:30 PM'
  ];

  const availableDates = Array.from({ length: 14 }, (_, i) => {
    const date = new Date();
    date.setDate(date.getDate() + i + 1); // Start from tomorrow
    return date.toISOString().split('T')[0];
  });

  return (
    <div className="bg-white rounded-xl shadow-soft p-6">
