components/dashboard/AddContentModal.js.content:
import React, { useState } from 'react';
import { XMarkIcon } from '@heroicons/react/24/outline';
import { supabase } from '../../lib/supabase';

export default function AddContentModal({ isOpen, onClose, onAdd, user, isTemporary }) {
  const [formData, setFormData] = useState({
    url: '',
    contentType: 'Money Page (Product/Service page)',
    goal: 'SEO (Rankings focused)',
    status: 'Active',
    targetKeyword: '',
    notes: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formErrors, setFormErrors] = useState({});

  if (!isOpen) return null;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when field is updated
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.url.trim()) {
      errors.url = 'URL is required';
    } else if (!formData.url.includes('.')) {
      errors.url = 'Please enter a valid URL';
    }

    if (!formData.targetKeyword.trim()) {
      errors.targetKeyword = 'Target keyword is required';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsSubmitting(true);

    try {
      // Store in Supabase if using permanent account
      if (!isTemporary && user) {
        // In a real implementation, you would insert to Supabase here
        await supabase.from('priority_content').insert({
          url: formData.url,
          content_type: formData.contentType,
          goal: formData.goal,
          status: formData.status,
          target_keyword: formData.targetKeyword,
          notes: formData.notes,
          user_id: user.id,
          is_temporary: false
        });
      } 
      // For temporary session
      else if (user && user.email) {
        // Store to temporary user table
        await supabase.from('priority_content').insert({
          url: formData.url,
          content_type: formData.contentType,
          goal: formData.goal,
          status: formData.status,
          target_keyword: formData.targetKeyword,
          notes: formData.notes,
          email: user.email,
          is_temporary: true
        });
      }

      onAdd();
      onClose();
    } catch (error) {
      console.error('Error adding content:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        <div 
          className="fixed inset-0 transition-opacity bg-black bg-opacity-50"
          onClick={onClose}
          aria-hidden="true"
        ></div>

        <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>

        <div className="inline-block overflow-hidden text-left align-bottom transition-all transform bg-white rounded-lg shadow-xl sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
          <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200">
            <h3 className="text-[18px] font-bold text-black">Add Priority Content</h3>
            <button 
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700"
            >
              <XMarkIcon className="w-5 h-5" />
            </button>
          </div>

          <div className="px-6 py-4">
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label htmlFor="url" className="block mb-1 text-[14px] font-semibold text-black">URL</label>
                <input 
                  id="url"
                  name="url"
                  type="text" 
                  placeholder="example.com/page" 
                  value={formData.url}
                  onChange={handleInputChange}
                  className={`w-full h-10 px-3 border ${formErrors.url ? 'border-red-500' : 'border-[#D1D5DB]'} rounded-md text-[14px] focus:outline-none focus:ring-1 focus:ring-black focus:border-black`}
                />
                {formErrors.url && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.url}</p>
                )}
              </div>

              <div className="mb-4">
                <label htmlFor="contentType" className="block mb-1 text-[14px] font-semibold text-black">Content Type</label>
                <select 
                  id="contentType"
                  name="contentType"
                  value={formData.contentType}
                  onChange={handleInputChange}
                  className="w-full h-10 px-3 border border-[#D1D5DB] rounded-md text-[14px] focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                >
                  <option value="Money Page (Product/Service page)">Money Page (Product/Service page)</option>
                  <option value="Case Study">Case Study</option>
                  <option value="Blog Post">Blog Post</option>
                  <option value="Landing Page">Landing Page</option>
                </select>
              </div>

              <div className="mb-4">
                <label htmlFor="goal" className="block mb-1 text-[14px] font-semibold text-black">Goal</label>
                <select 
                  id="goal"
                  name="goal"
                  value={formData.goal}
                  onChange={handleInputChange}
                  className="w-full h-10 px-3 border border-[#D1D5DB] rounded-md text-[14px] focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                >
                  <option value="SEO (Rankings focused)">SEO (Rankings focused)</option>
                  <option value="PR (Brand focused)">PR (Brand focused)</option>
                  <option value="Both">Both</option>
                </select>
              </div>

              <div className="mb-4">
                <label htmlFor="status" className="block mb-1 text-[14px] font-semibold text-black">Status</label>
                <select 
                  id="status"
                  name="status"
                  value={formData.status}
                  onChange={handleInputChange}
                  className="w-full h-10 px-3 border border-[#D1D5DB] rounded-md text-[14px] focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                >
                  <option value="Active">Active</option>
                  <option value="Paused">Paused</option>
                  <option value="Draft">Draft</option>
                </select>
              </div>

              <div className="mb-4">
                <label htmlFor="targetKeyword" className="block mb-1 text-[14px] font-semibold text-black">Target Keyword</label>
                <input 
                  id="targetKeyword"
                  name="targetKeyword"
                  type="text" 
                  placeholder="Your primary keyword" 
                  value={formData.targetKeyword}
                  onChange={handleInputChange}
                  className={`w-full h-10 px-3 border ${formErrors.targetKeyword ? 'border-red-500' : 'border-[#D1D5DB]'} rounded-md text-[14px] focus:outline-none focus:ring-1 focus:ring-black focus:border-black`}
                />
                {formErrors.targetKeyword && (
                  <p className="mt-1 text-sm text-red-600">{formErrors.targetKeyword}</p>
                )}
              </div>

              <div className="mb-4">
                <label htmlFor="notes" className="block mb-1 text-[14px] font-semibold text-black">Notes</label>
                <textarea 
                  id="notes"
                  name="notes"
                  placeholder="Special instructions or focus areas" 
                  value={formData.notes}
                  onChange={handleInputChange}
                  className="w-full h-20 px-3 py-2 border border-[#D1D5DB] rounded-md text-[14px] focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                ></textarea>
              </div>

              {isTemporary && (
                <div className="p-3 mb-4 text-sm text-[#92400E] bg-[#FFFBEB] border border-[#FEF3C7] rounded">
                  <p>
                    You're using a temporary session. Your content will be saved but to ensure long-term access, consider upgrading to a permanent account via the magic link option.
                  </p>
                </div>
              )}
            </form>
          </div>

          <div className="px-6 py-4 bg-gray-50 text-right">
            <button 
              onClick={onClose}
              className="px-4 py-2 mr-2 text-[14px] text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
              disabled={isSubmitting}
            >
              Cancel
            </button>
            <button 
              onClick={handleSubmit}
              disabled={isSubmitting}
              className={`px-4 py-2 text-[14px] text-white bg-black rounded-md hover:bg-gray-800 ${isSubmitting ? 'opacity-70 cursor-not-allowed' : ''}`}
            >
              {isSubmitting ? 'Adding...' : 'Add Content'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

components/dashboard/CampaignBuilder.js.content:
import React, { useState } from 'react';
import { CheckCircleIcon } from '@heroicons/react/24/solid';
import { ArrowDownTrayIcon } from '@heroicons/react/24/outline';

export default function CampaignBuilder() {
  const [expandedView, setExpandedView] = useState(false);
  const [campaignCreated, setCampaignCreated] = useState(false);
  const [selectedOptions, setSelectedOptions] = useState({
    goal: 'Both',
    budget: '$5,000',
    duration: '6 months',
    velocity: 'Balanced',
    articleType: 'Resources'
  });

  const handleOptionSelect = (category, option) => {
    setSelectedOptions({
      ...selectedOptions,
      [category]: option
    });
  };

  const handleDesignClick = () => {
    setExpandedView(true);
  };

  const handleSaveClick = () => {
    setCampaignCreated(true);
  };

  const handleCancelClick = () => {
    setExpandedView(false);
    setCampaignCreated(false);
  };

  const isOptionSelected = (category, option) => {
    return selectedOptions[category] === option;
  };

  if (!expandedView && !campaignCreated) {
    return (
      <div>
        <p className="text-[#6B7280] mb-8">
          Build your custom link building campaign based on your goals and budget
        </p>

        <button
          onClick={handleDesignClick}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-black hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-black"
        >
          Design Your Campaign
        </button>
      </div>
    );
  }

  if (campaignCreated) {
    return (
      <div>
        {/* Success Message */}
        <div className="mb-6 p-4 bg-[#DCFCE7] border border-[#86EFAC] rounded-md">
          <div className="flex items-start">
            <CheckCircleIcon className="h-5 w-5 text-green-500 mt-0.5 mr-2" />
            <div>
              <h3 className="text-sm font-bold text-green-800">Campaign Created Successfully</h3>
              <p className="text-sm text-green-700">Your campaign has been created and will be reviewed by our team.</p>
            </div>
          </div>
        </div>

        {/* Campaign Summary */}
        <div className="border border-[#E5E7EB] rounded-md p-6 mb-6">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-[18px] font-bold text-black">Campaign Summary</h3>
            <button className="text-[14px] text-[#2563EB] hover:underline">Edit Campaign</button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">Goal</p>
                <p className="text-[16px] font-medium text-black">{selectedOptions.goal} (Balanced)</p>
              </div>

              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">Monthly Budget</p>
                <p className="text-[16px] font-medium text-black">{selectedOptions.budget}</p>
              </div>

              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">Campaign Duration</p>
                <p className="text-[16px] font-medium text-black">{selectedOptions.duration}</p>
              </div>

              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">Link Velocity</p>
                <p className="text-[16px] font-medium text-black">{selectedOptions.velocity} (5-10 links/month)</p>
              </div>

              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">Type of Articles</p>
                <p className="text-[16px] font-medium text-black">{selectedOptions.articleType}</p>
              </div>
            </div>

            <div>
              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">Expected Links Per Month</p>
                <p className="text-[16px] font-medium text-black">5-10</p>
              </div>

              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">Average DR Range</p>
                <p className="text-[16px] font-medium text-black">55-65</p>
              </div>

              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">Timeline for SERP Impact</p>
                <p className="text-[16px] font-medium text-black">45-60 days</p>
              </div>

              <div className="mb-4">
                <p className="text-[14px] text-[#6B7280] mb-1">SEO vs Brand Authority Ratio</p>
                <p className="text-[16px] font-medium text-black">70% SEO, 30% brand authority</p>
              </div>
            </div>
          </div>

          <div className="flex justify-end mt-6">
            <button className="inline-flex items-center px-4 py-2 bg-black text-white text-[14px] font-medium rounded-md hover:bg-gray-800">
              <ArrowDownTrayIcon className="h-4 w-4 mr-2" />
              Download Summary
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Left Column - Campaign Configuration */}
        <div className="border border-[#E5E7EB] rounded-md p-6">
          <h3 className="text-[18px] font-bold text-black">Design your campaign</h3>
          <p className="text-[14px] text-[#6B7280] mb-6">Configure the key parameters for your link building campaign.</p>

          <div className="mb-6">
            <h4 className="text-[16px] font-bold text-black mb-1">Build Your Campaign</h4>
            <p className="text-[14px] text-[#6B7280] mb-4">Select parameters to create a custom link building strategy</p>
          </div>

          {/* Goals */}
          <div className="mb-6">
            <h4 className="text-[16px] font-semibold text-black mb-3">Goals</h4>
            <div className="grid grid-cols-3 gap-3">
              <div 
                onClick={() => handleOptionSelect('goal', 'SEO')}
                className={`cursor-pointer border ${isOptionSelected('goal', 'SEO') ? 'border-2 border-black' : 'border-[#E5E7EB]'} rounded-md p-3 relative`}
              >
                <h5 className="text-[14px] font-semibold mb-1">SEO</h5>
                <p className="text-[12px] text-[#6B7280]">Focus on rankings</p>
                {isOptionSelected('goal', 'SEO') && (
                  <div className="absolute bottom-1 right-1 w-2 h-2 bg-black rounded-full"></div>
                )}
              </div>
              <div 
                onClick={() => handleOptionSelect('goal', 'PR')}
                className={`cursor-pointer border ${isOptionSelected('goal', 'PR') ? 'border-2 border-black' : 'border-[#E5E7EB]'} rounded-md p-3 relative`}
              >
                <h5 className="text-[14px] font-semibold mb-1">PR</h5>
                <p className="text-[12px] text-[#6B7280]">Brand authority</p>
                {isOptionSelected('goal', 'PR') && (
                  <div className="absolute bottom-1 right-1 w-2 h-2 bg-black rounded-full"></div>
                )}
              </div>
              <div 
                onClick={() => handleOptionSelect('goal', 'Both')}
                className={`cursor-pointer border ${isOptionSelected('goal', 'Both') ? 'border-2 border-black' : 'border-[#E5E7EB]'} rounded-md p-3 relative`}
              >
                <h5 className="text-[14px] font-semibold mb-1">Both</h5>
                <p className="text-[12px] text-[#6B7280]">Balanced</p>
                {isOptionSelected('goal', 'Both') && (
                  <div className="absolute bottom-1 right-1 w-2 h-2 bg-black rounded-full"></div>
                )}
              </div>
            </div>
          </div>

          {/* Monthly Budget */}
          <div className="mb-6">
            <h4 className="text-[16px] font-semibold text-black mb-3">Monthly Budget</h4>
            <div className="grid grid-cols-3 gap-3">
              <div 
                onClick={() => handleOptionSelect('budget', '$2,500')}
                className={`cursor-pointer border ${isOptionSelected('budget', '$2,500') ? 'border-2 border-black' : 'border-[#E5E7EB]'} rounded-md p-3 relative`}
              >
                <h5 className="text-[14px] font-semibold mb-1">$2,500</h5>
                <p className="text-[12px] text-[#6B7280]">3-7 links</p>
                {isOptionSelected('budget', '$2,500') && (
                  <div className="absolute bottom-1 right-1 w-2 h-2 bg-black rounded-full"></div>
                )}
              </div>
              <div 
                onClick={() => handleOptionSelect('budget', '$5,000')}
                className={`cursor-pointer border ${isOptionSelected('budget', '$5,000') ? 'border-2 border-black' : 'border-[#E5E7EB]'} rounded-md p-3 relative`}
              >
                <h5 className="text-[14px] font-semibold mb-1">$5,000</h5>
                <p className="text-[12px] text-[#6B7280]">8-13 links</p>
                {isOptionSelected('budget', '$5,000') && (
                  <div className="absolute bottom-1 right-1 w-2 h-2 bg-black rounded-full"></div>
                )}
              </div>
