import React, { useState } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/router';
import { useAuth } from '../../context/AuthContext';
import { 
  Bars3Icon, 
  XMarkIcon, 
  UserIcon, 
  Cog6ToothIcon,
  DocumentDuplicateIcon,
  BuildingLibraryIcon,
  ChartBarIcon,
  LinkIcon,
  ArrowRightOnRectangleIcon
} from '@heroicons/react/24/outline';
import LoadingSpinner from '../ui/LoadingSpinner';

export default function ClientDashboardLayout({ children, title }) {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const { user, signOut } = useAuth();
  const router = useRouter();

  const handleLogoutClick = () => {
    setShowLogoutModal(true);
  };

  const cancelLogout = () => {
    setShowLogoutModal(false);
  };

  const confirmLogout = async () => {
    try {
      const { success, error } = await signOut();
      if (success) {
        router.push('/auth');
      } else if (error) {
        console.error('Logout error:', error);
      }
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      setShowLogoutModal(false);
    }
  };

  const navigation = [
    { name: 'Priority Content', href: '/dashboard/priority-content', icon: DocumentDuplicateIcon },
    { name: 'Campaign Builder', href: '/dashboard/campaign-builder', icon: BuildingLibraryIcon },
    { name: 'Link Progress', href: '/dashboard/link-progress', icon: ChartBarIcon },
    { name: 'Link Domains', href: '/dashboard/link-domains', icon: LinkIcon },
    { name: 'Settings', href: '/dashboard/settings', icon: Cog6ToothIcon },
  ];

  return (
    <div className="relative min-h-screen">
      {/* Mobile sidebar */}
      <div className={`fixed inset-0 flex z-40 md:hidden ${sidebarOpen ? 'block' : 'hidden'}`}>
        <div 
          className="fixed inset-0 bg-gray-600 bg-opacity-75" 
          onClick={() => setSidebarOpen(false)}
        />
        <div className="relative flex-1 flex flex-col max-w-xs w-full bg-white">
          <div className="absolute top-0 right-0 -mr-12 pt-2">
            <button 
              type="button" 
              className="ml-1 flex items-center justify-center h-10 w-10 rounded-full focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white"
              onClick={() => setSidebarOpen(false)}
            >
              <span className="sr-only">Close sidebar</span>
              <XMarkIcon className="h-6 w-6 text-white" aria-hidden="true" />
            </button>
          </div>

          <div className="flex-1 h-0 pt-5 pb-4 overflow-y-auto">
            <div className="flex-shrink-0 flex items-center px-4">
              <span className="text-xl font-bold">GrowthOG</span>
            </div>
            <nav className="mt-5 px-2 space-y-1">
              {navigation.map((item) => (
                <Link 
                  key={item.name} 
                  href={item.href}
                  className="group flex items-center px-2 py-2 text-base font-medium rounded-md text-gray-600 hover:bg-gray-50 hover:text-gray-900"
                >
                  <item.icon className="mr-4 h-6 w-6 text-gray-400 group-hover:text-gray-500" aria-hidden="true" />
                  {item.name}
                </Link>
              ))}
            </nav>
          </div>

          <div className="flex-shrink-0 flex flex-col border-t border-gray-200 p-4">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <span className="inline-flex items-center justify-center h-10 w-10 rounded-full bg-gray-500">
                  <span className="text-sm font-medium leading-none text-white">
                    {user?.user_metadata?.name ? user.user_metadata.name.charAt(0) : 'U'}
                  </span>
                </span>
              </div>
              <div className="ml-3">
                <p className="text-base font-medium text-gray-700">
                  {user?.user_metadata?.name || 'User'}
                </p>
                <p className="text-sm font-medium text-gray-500">
                  {user?.email || 'user@example.com'}
                </p>
              </div>
            </div>
            <button
              onClick={handleLogoutClick}
              className="mt-4 flex items-center px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-md"
            >
              <ArrowRightOnRectangleIcon className="mr-2 h-5 w-5 text-gray-500" aria-hidden="true" />
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* Static sidebar for desktop */}
      <div className="hidden md:flex md:w-64 md:flex-col md:fixed md:inset-y-0">
        <div className="flex-1 flex flex-col min-h-0 border-r border-gray-200 bg-white">
          <div className="flex-1 flex flex-col pt-5 pb-4 overflow-y-auto">
            <div className="flex items-center flex-shrink-0 px-4">
              <span className="text-xl font-bold">GrowthOG</span>
            </div>
            <nav className="mt-5 flex-1 px-2 bg-white space-y-1">
              {navigation.map((item) => (
                <Link 
                  key={item.name} 
                  href={item.href}
                  className="group flex items-center px-2 py-2 text-sm font-medium rounded-md text-gray-600 hover:bg-gray-50 hover:text-gray-900"
                >
                  <item.icon className="mr-3 h-6 w-6 text-gray-400 group-hover:text-gray-500" aria-hidden="true" />
                  {item.name}
                </Link>
              ))}
            </nav>
          </div>
          <div className="flex-shrink-0 flex flex-col border-t border-gray-200 p-4">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <span className="inline-flex items-center justify-center h-8 w-8 rounded-full bg-gray-500">
                  <span className="text-sm font-medium leading-none text-white">
                    {user?.user_metadata?.name ? user.user_metadata.name.charAt(0) : 'U'}
                  </span>
                </span>
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-gray-700">
                  {user?.user_metadata?.name || 'User'}
                </p>
                <p className="text-xs font-medium text-gray-500">
                  {user?.email || 'user@example.com'}
                </p>
              </div>
            </div>
            <button
              onClick={handleLogoutClick}
              className="mt-3 flex items-center px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-md"
            >
              <ArrowRightOnRectangleIcon className="mr-2 h-5 w-5 text-gray-500" aria-hidden="true" />
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* Main content */}
      <div className="md:pl-64">
        <div className="mx-auto flex flex-col">
          {/* Top nav */}
          <div className="sticky top-0 z-10 md:hidden pl-1 pt-1 sm:pl-3 sm:pt-3 bg-white border-b border-gray-200">
            <button
              type="button"
              className="-ml-0.5 -mt-0.5 h-12 w-12 inline-flex items-center justify-center rounded-md text-gray-500 hover:text-gray-900"
              onClick={() => setSidebarOpen(true)}
            >
              <span className="sr-only">Open sidebar</span>
              <Bars3Icon className="h-6 w-6" aria-hidden="true" />
            </button>
          </div>

          {/* Content */}
          <main className="flex-1">
            <div className="py-6">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 md:px-8">
                <h1 className="text-2xl font-semibold text-gray-900">{title}</h1>
              </div>
              <div className="max-w-7xl mx-auto px-4 sm:px-6 md:px-8 mt-4">
                {isLoading ? (
                  <div className="flex items-center justify-center h-64">
                    <LoadingSpinner size="large" />
                  </div>
                ) : (
                  children
                )}
              </div>
            </div>
          </main>
        </div>
      </div>

      {/* Logout Confirmation Modal */}
      {showLogoutModal && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-40">
          <div className="bg-white rounded-md p-6 max-w-md w-full">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Are you sure?</h3>
            <p className="text-[14px] text-gray-600 mb-2">Your progress is not saved.</p>
            <p className="text-[14px] text-gray-600 mb-4">To save your progress, login using the magic link.</p>

            <button 
              className="w-full mb-4 flex justify-center items-center px-4 py-2 border border-transparent text-[14px] font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
            >
              <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"></path>
              </svg>
              Send Magic Link
            </button>

            <div className="flex justify-between">
              <button 
                onClick={cancelLogout}
                className="flex-1 mr-2 px-4 py-2 border border-gray-300 text-[14px] font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
              >
                Cancel
              </button>
              <button 
                onClick={confirmLogout}
                className="flex-1 ml-2 px-4 py-2 border border-transparent text-[14px] font-medium rounded-md text-white bg-red-600 hover:bg-red-700"
              >
                Log out anyway
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

components/dashboard/DashboardHeader.js.content:
import React, { useState } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/router';
import { signOut } from '../../lib/supabase';

export default function DashboardHeader({ user, isTemporary }) {
  const router = useRouter();
  const currentPath = router.pathname;
  const [showUserMenu, setShowUserMenu] = useState(false);

  const tabs = [
    { name: 'PRIORITY CONTENT', href: '/dashboard/priority-content' },
    { name: 'CAMPAIGN BUILDER', href: '/dashboard/campaign-builder' },
    { name: 'LINK PROGRESS', href: '/dashboard/link-progress' },
    { name: 'LINK DOMAINS', href: '/dashboard/link-domains' },
    { name: 'SETTINGS', href: '/dashboard/settings' },
  ];

  // Handle sign out
  const handleSignOut = async () => {
    await signOut();
    router.push('/auth');
  };

  // Get user's display name or email
  const displayName = user?.name || user?.email?.split('@')[0] || 'User';

  // Get user's initials for the avatar
  const getInitials = () => {
    if (user?.name) {
      const names = user.name.split(' ');
      if (names.length >= 2) {
        return `${names[0][0]}${names[1][0]}`.toUpperCase();
      }
      return user.name[0].toUpperCase();
    }
    return user?.email?.[0].toUpperCase() || 'U';
  };

  return (
    <div>
      {/* Top section with user info */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-[32px] font-extrabold text-black mb-2">Client Dashboard</h1>
          <div>
            <span className="text-[16px] text-[#2563EB] align-middle">Your link building campaign at a glance</span>
            {!user && (
              <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded text-[12px] font-semibold bg-black text-white">
                BETA
              </span>
            )}
            {isTemporary && (
              <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded text-[12px] font-semibold bg-[#FEF3C7] text-[#92400E]">
                TEMPORARY
              </span>
            )}
          </div>
        </div>

        {/* User Menu */}
        <div className="relative">
          <button 
            onClick={() => setShowUserMenu(!showUserMenu)}
            className="flex items-center space-x-2 text-sm focus:outline-none"
          >
            <div className="h-8 w-8 rounded-full bg-[#0F172A] flex items-center justify-center text-white text-sm font-medium">
              {getInitials()}
            </div>
            <span className="font-medium text-gray-700">{displayName}</span>
            <svg className="h-4 w-4 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {/* Dropdown Menu */}
          {showUserMenu && (
            <div className="absolute right-0 mt-2 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-10">
              <div className="py-1">
                <Link 
                  href="/dashboard/settings" 
                  className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                  onClick={() => setShowUserMenu(false)}
                >
                  Settings
                </Link>
                <button
                  onClick={handleSignOut}
                  className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                >
                  Sign out
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="bg-[#F9FAFB] rounded-lg overflow-hidden mb-6">
        <div className="flex w-full">
          {tabs.map((tab) => {
            const isActive = currentPath === tab.href || 
                            (currentPath === '/dashboard' && tab.href === '/dashboard/priority-content');

            return (
              <Link 
                key={tab.name} 
                href={tab.href}
                className={`
                  flex-1 py-3 px-1 text-center text-[14px] font-medium
                  ${isActive
                    ? 'bg-white text-[#2563EB] font-semibold border-b-2 border-[#2563EB]'
                    : 'text-[#6B7280] hover:text-gray-700'
                  }
                `}
              >
                {tab.name}
              </Link>
            );
          })}
        </div>
      </div>
    </div>
  );
}

