components/dashboard/DashboardTabs.js.content:
import React, { useState } from 'react';
import { useRouter } from 'next/router';
import Link from 'next/link';
import { 
  DocumentDuplicateIcon, 
  BuildingLibraryIcon, 
  ChartBarIcon, 
  LinkIcon,
  Cog6ToothIcon
} from '@heroicons/react/24/outline';

export default function DashboardTabs() {
  const router = useRouter();
  const { pathname } = router;

  const tabs = [
    { 
      name: 'Priority Content', 
      href: '/dashboard/priority-content', 
      icon: DocumentDuplicateIcon,
      current: pathname === '/dashboard/priority-content'
    },
    { 
      name: 'Campaign Builder', 
      href: '/dashboard/campaign-builder', 
      icon: BuildingLibraryIcon,
      current: pathname === '/dashboard/campaign-builder'
    },
    { 
      name: 'Link Progress', 
      href: '/dashboard/link-progress', 
      icon: ChartBarIcon,
      current: pathname === '/dashboard/link-progress'
    },
    { 
      name: 'Link Domains', 
      href: '/dashboard/link-domains', 
      icon: LinkIcon,
      current: pathname === '/dashboard/link-domains'
    },
    { 
      name: 'Settings', 
      href: '/dashboard/settings', 
      icon: Cog6ToothIcon,
      current: pathname === '/dashboard/settings'
    },
  ];

  return (
    <div className="border-b-[2.5px] border-black">
      <nav className="-mb-px flex space-x-8" aria-label="Tabs">
        {tabs.map((tab) => (
          <Link
            key={tab.name}
            href={tab.href}
            className={`
              group flex items-center whitespace-nowrap py-4 px-1 border-b-[2.5px] font-medium text-sm
              ${tab.current
                ? 'border-black text-black'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}
            `}
            aria-current={tab.current ? 'page' : undefined}
          >
            <tab.icon
              className={`
                mr-2 h-5 w-5 flex-shrink-0
                ${tab.current ? 'text-black' : 'text-gray-400 group-hover:text-gray-500'}
              `}
              aria-hidden="true"
            />
            {tab.name}
          </Link>
        ))}
      </nav>
    </div>
  );
}

components/dashboard/LinkProgressTable.j.content:
import React from 'react';

function StatusBadge({ status }) {
  let bgColor, textColor;

  switch (status) {
    case 'Live':
      bgColor = 'bg-green-100';
      textColor = 'text-green-800';
      break;
    case 'Submitted':
      bgColor = 'bg-blue-100';
      textColor = 'text-blue-800';
      break;
    case 'Progress':
      bgColor = 'bg-yellow-100';
      textColor = 'text-yellow-800';
      break;
    case 'Planned':
      bgColor = 'bg-gray-100';
      textColor = 'text-gray-800';
      break;
    default:
      bgColor = 'bg-gray-100';
      textColor = 'text-gray-800';
  }

  return (
    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${bgColor} ${textColor}`}>
      {status}
    </span>
  );
}

export default function LinkProgressTable({ links = [], pagination = {}, onPageChange }) {
  return (
    <div>
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Domain
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                DR
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Link Type
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Content
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Status
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Date Updated
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {links.length > 0 ? (
              links.map((link) => (
                <tr key={link.id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-indigo-600 hover:text-indigo-900">
                    <a href={link.url} target="_blank" rel="noopener noreferrer">
                      {link.domain}
                    </a>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-green-600 font-medium">
                    {link.dr}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {link.linkType}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {link.content}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <StatusBadge status={link.status} />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {link.dateUpdated}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 text-center">
                  No links found. Links will appear here once your campaign is running.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {links.length > 0 && (
        <div className="flex items-center justify-between border-t border-gray-200 bg-white px-4 py-3 sm:px-6 mt-4">
          <div className="flex flex-1 justify-between sm:hidden">
            <button 
              onClick={() => onPageChange && onPageChange(Math.max(1, (pagination.currentPage || 1) - 1))}
              className="relative inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50"
            >
              Previous
            </button>
            <button 
              onClick={() => onPageChange && onPageChange((pagination.currentPage || 1) + 1)}
              className="relative ml-3 inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50"
            >
              Next
            </button>
          </div>
          <div className="hidden sm:flex sm:flex-1 sm:items-center sm:justify-between">
            <div>
              <p className="text-sm text-gray-700">
                Page <span className="font-medium">{pagination.currentPage || 1}</span> of <span className="font-medium">{pagination.totalPages || 1}</span>
              </p>
            </div>
            <div>
              <nav className="isolate inline-flex -space-x-px rounded-md shadow-sm" aria-label="Pagination">
                <button 
                  className="relative inline-flex items-center rounded-l-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0"
                  onClick={() => onPageChange && onPageChange(Math.max(1, (pagination.currentPage || 1) - 1))}
                >
                  <span className="sr-only">Previous</span>
                  Previous
                </button>
                <button
                  className="relative inline-flex items-center rounded-r-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0"
                  onClick={() => onPageChange && onPageChange((pagination.currentPage || 1) + 1)}
                >
                  <span className="sr-only">Next</span>
                  Next
                </button>
              </nav>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

