          </div>

          {/* Navigation */}
          <nav className="flex-1 px-2 py-4 space-y-1 overflow-y-auto">
            {navigation.map((item) => (
              <Link
                key={item.name}
                href={item.href}
                className={`
                  group flex items-center px-3 py-2 text-sm font-medium rounded-md 
                  ${isActive(item.href) 
                    ? 'bg-primary-50 text-primary-600' 
                    : 'text-secondary-700 hover:bg-secondary-50 hover:text-secondary-900'}
                `}
              >
                <item.icon 
                  className={`
                    flex-shrink-0 mr-3 h-5 w-5 
                    ${isActive(item.href) ? 'text-primary-500' : 'text-secondary-500 group-hover:text-secondary-500'}
                  `}
                  aria-hidden="true" 
                />
                {item.name}
              </Link>
            ))}
          </nav>

          {/* Logout button */}
          <div className="px-2 py-4 border-t border-secondary-200">
            <button
              onClick={handleLogout}
              className="w-full group flex items-center px-3 py-2 text-sm font-medium rounded-md text-red-600 hover:bg-red-50"
            >
              <ArrowLeftOnRectangleIcon 
                className="flex-shrink-0 mr-3 h-5 w-5 text-red-500"
                aria-hidden="true" 
              />
              Logout
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default DashboardSidebar;

components/layout/Layout.js.content:
import React from 'react';
import Head from 'next/head';
import Navbar from './Navbar';
import Footer from './Footer';

const Layout = ({ children, seo = {} }) => {
  // Default SEO values
  const defaultSEO = {
    title: 'GrowthOG - Links That Drive SaaS Growth',
    description: 'We don\'t just build links. We create authority that fuels organic growth for B2B SaaS brands.',
    canonical: 'https://www.growthog.com',
    openGraph: {
      type: 'website',
      locale: 'en_US',
      url: 'https://www.growthog.com',
      site_name: 'GrowthOG',
      images: [
        {
          url: 'https://www.growthog.com/images/og-image.jpg',
          width: 1200,
          height: 630,
          alt: 'GrowthOG',
        },
      ],
    },
    twitter: {
      handle: '@growthog',
      site: '@growthog',
      cardType: 'summary_large_image',
    },
    noindex: false, // Default is to allow indexing
  };

  // Merge default with page-specific SEO values
  const mergedSEO = { ...defaultSEO, ...seo };

  return (
    <>
      <Head>
        <title>{mergedSEO.title}</title>
        <meta name="description" content={mergedSEO.description} />
        <link rel="canonical" href={mergedSEO.canonical} />
        <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=5, viewport-fit=cover" />

        {/* Control indexing */}
        {mergedSEO.noindex ? (
          <meta name="robots" content="noindex, nofollow" />
        ) : (
          <meta name="robots" content="index, follow, max-image-preview:large, max-snippet:-1, max-video-preview:-1" />
        )}

        {/* Language tag for accessibility and SEO */}
        <meta property="og:locale" content={mergedSEO.openGraph?.locale || defaultSEO.openGraph.locale} />

        {/* Open Graph */}
        <meta property="og:type" content={mergedSEO.openGraph?.type || defaultSEO.openGraph.type} />
        <meta property="og:url" content={mergedSEO.openGraph?.url || defaultSEO.openGraph.url} />
        <meta property="og:title" content={mergedSEO.title} />
        <meta property="og:description" content={mergedSEO.description} />
        <meta property="og:image" content={mergedSEO.openGraph?.images?.[0]?.url || defaultSEO.openGraph.images[0].url} />
        <meta property="og:site_name" content={mergedSEO.openGraph?.site_name || defaultSEO.openGraph.site_name} />

        {/* Image dimensions for better Open Graph display */}
        {mergedSEO.openGraph?.images?.[0]?.width && (
          <meta property="og:image:width" content={mergedSEO.openGraph.images[0].width.toString()} />
        )}
        {mergedSEO.openGraph?.images?.[0]?.height && (
          <meta property="og:image:height" content={mergedSEO.openGraph.images[0].height.toString()} />
        )}

        {/* Twitter */}
        <meta name="twitter:card" content={mergedSEO.twitter?.cardType || defaultSEO.twitter.cardType} />
        <meta name="twitter:site" content={mergedSEO.twitter?.site || defaultSEO.twitter.site} />
        <meta name="twitter:title" content={mergedSEO.title} />
        <meta name="twitter:description" content={mergedSEO.description} />
        <meta name="twitter:image" content={mergedSEO.openGraph?.images?.[0]?.url || defaultSEO.openGraph.images[0].url} />

        {/* Favicons */}
        <link rel="icon" href="/favicon.ico" />

        {/* Preconnect to headless WordPress domain for faster loading */}
        <link rel="preconnect" href="https://headlesswp.growthog.com" />
        <link rel="dns-prefetch" href="https://headlesswp.growthog.com" />
      </Head>

      <div className="flex flex-col min-h-screen">
        <Navbar />
        <main className="flex-grow mt-2">{children}</main>
        <Footer />
      </div>
    </>
  );
};

export default Layout;

components/layout/Navbar.js.content:
import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import { useAuth } from '../../context/AuthContext';
import BookStrategyCallModal from '../BookStrategyCallModal';

const Navbar = () => {
  const { user } = useAuth();
  const [resourcesOpen, setResourcesOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Check if mobile on client-side only
  useEffect(() => {
    function handleResize() {
      setIsMobile(window.innerWidth < 1024);
    }

    // Initial check
    handleResize();

    // Add listener for window resize
    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Handle dropdown toggle
  const toggleResources = (e) => {
    if (e) e.preventDefault(); // Prevent default only if event is provided
    setResourcesOpen(!resourcesOpen);
  };

  // Toggle mobile menu
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);

    // Close resources dropdown when main menu is toggled
    if (resourcesOpen) {
      setResourcesOpen(false);
    }
  };

  // Open the book strategy call modal
  const openBookCallModal = () => {
    setIsModalOpen(true);

    // Close mobile menu when opening modal
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }
  };

  // Close the book strategy call modal
  const closeBookCallModal = () => {
    setIsModalOpen(false);
  };

  return (
    <>
      <nav className="py-4 border-b-[2.5px] border-black relative z-30">
        <div className="max-w-[1280px] mx-auto px-4 sm:px-8">
          <div className="flex justify-between items-center">
            {/* Logo */}
            <div className="flex-shrink-0">
              <Link href="/" className="text-black font-semibold text-xl w-[140px] inline-block">
                GrowthOG
              </Link>
            </div>

            {/* Mobile Menu Button */}
            <div className="lg:hidden">
              <button
                onClick={toggleMobileMenu}
                className="text-black focus:outline-none"
                aria-label="Toggle menu"
              >
                {isMobileMenuOpen ? (
                  // X icon
                  <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                ) : (
                  // Hamburger icon
                  <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                  </svg>
                )}
              </button>
            </div>

            {/* Desktop Navigation - Hidden on Mobile */}
            <div className="hidden lg:flex items-center space-x-6">
              <Link 
                href="/services" 
                className="text-[#4B5563] hover:text-black text-base font-medium"
              >
                Services
              </Link>
              <Link 
                href="/pricing" 
                className="text-[#4B5563] hover:text-black text-base font-medium"
              >
                Pricing
              </Link>

              {/* Resources dropdown */}
              <div className="relative">
                <div className="flex items-center">
                  <Link 
                    href="/resources/" 
                    className="text-[#4B5563] hover:text-black text-base font-medium"
                  >
                    Resources
                  </Link>
                  <button 
                    className="ml-1 text-[#4B5563] hover:text-black flex items-center"
                    onClick={(e) => toggleResources(e)}
                  >
                    <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={resourcesOpen ? "M5 15l7-7 7 7" : "M19 9l-7 7-7-7"} />
                    </svg>
                  </button>
                </div>

                {/* Dropdown menu */}
                {resourcesOpen && (
                  <div className="absolute mt-2 w-48 bg-white rounded-md shadow-lg py-1 z-20">
                    <Link href="/case-studies/" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                      Case Studies
                    </Link>
                    <Link href="/link-building-blog/" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                      Blog
                    </Link>
                  </div>
                )}
              </div>

              <Link 
                href="/about-us" 
                className="text-[#4B5563] hover:text-black text-base font-medium"
              >
                About Us
              </Link>

              <Link 
                href="/auth/" 
                className="text-black border border-[#E5E7EB] rounded px-4 py-2 text-base font-medium hover:bg-gray-50"
              >
                Access Dashboard
              </Link>

              <button 
                onClick={openBookCallModal}
                className="bg-black text-white rounded-md px-4 py-2 text-base font-medium hover:bg-[#333333]"
              >
                Book a Strategy Call
              </button>
            </div>
          </div>
        </div>

        {/* Mobile Menu - Slide in from top */}
        {isMobileMenuOpen && (
          <div className="lg:hidden fixed inset-0 z-20 mt-[66px]">
            {/* Background overlay */}
            <div className="absolute inset-0 bg-black opacity-50" onClick={toggleMobileMenu}></div>

            {/* Menu content */}
            <div className="relative bg-white h-auto border-b border-gray-200 shadow-lg pt-4 pb-6 px-8 z-30">
              <div className="flex flex-col space-y-4">
                <Link 
                  href="/services" 
                  className="text-[#4B5563] hover:text-black text-lg font-medium py-2"
                  onClick={toggleMobileMenu}
                >
                  Services
                </Link>
                <Link 
                  href="/pricing" 
                  className="text-[#4B5563] hover:text-black text-lg font-medium py-2"
                  onClick={toggleMobileMenu}
                >
                  Pricing
                </Link>

                {/* Resources collapsible section */}
                <div>
                  <div className="flex items-center justify-between w-full">
                    {/* Link to /resources/ page */}
                    <Link 
                      href="/resources/" 
                      className="text-[#4B5563] hover:text-black text-lg font-medium py-2 flex-grow text-left"
                      onClick={toggleMobileMenu}
                    >
                      Resources
                    </Link>

                    {/* Dropdown button (separate from the link) */}
                    <button 
                      className="text-[#4B5563] hover:text-black ml-2 p-2"
                      onClick={(e) => {
                        e.preventDefault(); // Prevent any navigation
                        toggleResources();
                      }}
                    >
                      <svg className="h-5 w-5 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24"
                        style={{ transform: resourcesOpen ? 'rotate(180deg)' : 'rotate(0)' }}>
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
                      </svg>
                    </button>
                  </div>

                  {/* Collapsible content */}
                  {resourcesOpen && (
                    <div className="pl-4 py-2 flex flex-col space-y-3">
                      <Link 
                        href="/case-studies/" 
                        className="text-[#4B5563] hover:text-black text-base py-1"
                        onClick={toggleMobileMenu}
                      >
                        Case Studies
                      </Link>
                      <Link 
                        href="/link-building-blog/" 
                        className="text-[#4B5563] hover:text-black text-base py-1"
                        onClick={toggleMobileMenu}
                      >
                        Blog
                      </Link>
                    </div>
                  )}
                </div>

                <Link 
                  href="/about-us" 
                  className="text-[#4B5563] hover:text-black text-lg font-medium py-2"
                  onClick={toggleMobileMenu}
                >
                  About Us
                </Link>

                <Link 
                  href="/auth/" 
                  className="text-black border border-[#E5E7EB] rounded-md px-4 py-3 text-base font-medium hover:bg-gray-50 text-center mt-2"
                  onClick={toggleMobileMenu}
                >
                  Access Dashboard
                </Link>

                <button 
                  onClick={openBookCallModal}
                  className="bg-black text-white rounded-md px-4 py-3 text-base font-medium hover:bg-[#333333] mt-2"
                >
                  Book a Strategy Call
                </button>
              </div>
            </div>
          </div>
        )}
      </nav>

      {/* Book Strategy Call Modal */}
      <BookStrategyCallModal 
        isOpen={isModalOpen} 
        onClose={closeBookCallModal} 
      />
    </>
  );
};

export default Navbar;

components/pricing/FAQ.js.content:
import React, { useState } from 'react';
import { ChevronDownIcon, ChevronUpIcon } from '@heroicons/react/24/outline';

const FAQ = ({ questions }) => {
  const [openIndex, setOpenIndex] = useState(0);

  const toggleQuestion = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className="mt-12 space-y-4">
      <h2 className="text-2xl font-bold text-secondary-900 mb-6">Frequently Asked Questions</h2>

      {questions.map((question, index) => (
        <div 
          key={index} 
          className={`border rounded-lg overflow-hidden ${
            openIndex === index ? 'border-primary-300' : 'border-secondary-200'
          }`}
        >
          <button
            onClick={() => toggleQuestion(index)}
            className="flex justify-between items-center w-full px-6 py-4 text-left focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
          >
            <span className="text-lg font-medium text-secondary-900">{question.question}</span>
            {openIndex === index ? (
              <ChevronUpIcon className="h-5 w-5 text-primary-500" />
            ) : (
              <ChevronDownIcon className="h-5 w-5 text-secondary-500" />
            )}
          </button>

          {openIndex === index && (
            <div className="px-6 pb-4">
              <p className="text-secondary-600">{question.answer}</p>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default FAQ;

