pages/api/supabase-config.js.content:
import { updateSupabaseConfig, getSupabaseClient } from '../../lib/supabase';

export default function handler(req, res) {
  // Handle GET requests to retrieve (but not expose) if config exists
  if (req.method === 'GET') {
    try {
      // Don't return the actual keys for security, just indicate if they exist
      const supabase = getSupabaseClient();
      return res.status(200).json({
        configured: !!supabase,
        url: supabase ? '********' : '',
        key: supabase ? '********' : ''
      });
    } catch (error) {
      console.error('Error fetching Supabase config:', error);
      return res.status(500).json({
        success: false,
        message: 'An error occurred while retrieving Supabase configuration'
      });
    }
  }

  // Handle POST requests
  if (req.method !== 'POST') {
    return res.status(405).json({ message: 'Method not allowed' });
  }

  try {
    // Accept either field name format for compatibility
    const url = req.body.url || req.body.supabaseUrl;
    const key = req.body.key || req.body.supabaseKey;

    // Validate inputs
    if (!url || !key) {
      return res.status(400).json({
        success: false,
        message: 'Both Supabase URL and API key are required'
      });
    }

    // Update the Supabase configuration
    const success = updateSupabaseConfig(url, key);

    if (success) {
      return res.status(200).json({
        success: true,
        message: 'Supabase configuration updated successfully'
      });
    } else {
      return res.status(400).json({
        success: false,
        message: 'Failed to update Supabase configuration'
      });
    }
  } catch (error) {
    console.error('Error updating Supabase config:', error);
    return res.status(500).json({
      success: false,
      message: 'An error occurred while updating Supabase configuration'
    });
  }
}

