import { useEffect } from 'react';
import { useRouter } from 'next/router';
import { getSupabaseClient } from '../../lib/supabase';

export default function AuthCallback() {
  const router = useRouter();

  useEffect(() => {
    // Get the Supabase client
    const supabase = getSupabaseClient();
    if (!supabase) {
      console.error('Supabase client not initialized');
      router.push('/dashboard');
      return;
    }

    // Handle the OAuth callback or magic link
    const handleAuthCallback = async () => {
      try {
        // Get the hash from the URL
        const hash = window.location.hash;

        // Process the hash and exchange it for a session
        if (hash) {
          const { error } = await supabase.auth.getSessionFromUrl();

          if (error) {
            throw error;
          }

          // Success, redirect to dashboard
          router.push('/dashboard/priority-content');
        } else {
          // No hash, check if there's a code in the query params
          const { code } = router.query;

          if (code) {
            // Process the code
            const { error } = await supabase.auth.exchangeCodeForSession(code);

            if (error) {
              throw error;
            }

            // Success, redirect to dashboard
            router.push('/dashboard/priority-content');
          } else {
            // No hash or code, redirect to dashboard
            router.push('/dashboard');
          }
        }
      } catch (error) {
        console.error('Error processing authentication:', error);
        router.push('/dashboard'); // Redirect to dashboard on error
      }
    };

    // Only run when router is ready
    if (router.isReady) {
      handleAuthCallback();
    }
  }, [router, router.isReady, router.query]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-secondary-50">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-600 mx-auto mb-4"></div>
        <h1 className="text-xl font-semibold text-secondary-900">Completing login...</h1>
        <p className="text-secondary-600 mt-2">Please wait while we complete your authentication.</p>
      </div>
    </div>
  );
}

