              <span className="border border-gray-700 rounded-full px-4 py-1">{caseStudy.industry}</span>
            </div>
            <h1 className="text-2xl md:text-4xl lg:text-5xl font-bold mb-6 text-white leading-tight">{caseStudy.title}</h1>
