                    <a
                      key={index}
                      href={`#${item.id}`}
                      className={`block text-sm py-1 hover:text-primary-600 transition-colors ${
                        activeHeading === item.id ? 'text-primary-600 font-medium' : 'text-secondary-600'
                      }`}
                    >
                      {item.title}
                    </a>
                  ))}
