        {/* Add Content Modal */}
        <AddContentModal 
          isOpen={isAddModalOpen}
          onClose={() => setIsAddModalOpen(false)}
          onAdd={handleAddContent}
          user={user}
          isTemporary={isTemporary}
        />
      </div>
    </DashboardMainLayout>
  );
}

